mapper.select:
SELECT COUNT(*) 
FROM employee 
WHERE empl_id = #{id}


SELECT empl_pw 
FROM employee 
WHERE empl_id = #{id}


SELECT empl_changepw
FROM employee 
WHERE empl_id = #{id}


SELECT empl_id 
FROM employee 
WHERE empl_name = #{name} AND empl_per_email = #{email}

SELECT COUNT(*) 
FROM employee 
WHERE empl_name = #{name}

mapper.update:

UPDATE employee 
SET empl_pw = #{encryptPw} 
WHERE empl_id = #{id}


UPDATE employee 
SET empl_changepw = 1 
WHERE empl_id = #{id}

UPDATE employee
empl_name = #{empl_name}, bank_idx = #{bank_idx}, empl_account = #{empl_account}, empl_per_email = #{empl_per_email}, empl_per_phone = #{empl_per_phone}, empl_addr = #{empl_addr}, empl_profile = #{empl_profile},
WHERE empl_id = #{empl_id}
